var lines = input.Split("\n", StringSplitOptions.RemoveEmptyEntries);
var ySize = lines.Length;
var xSize = lines[0].Length - 1;

char[,] xmap = new char[xSize, ySize];

for (int y = 0; y < ySize; y++)
{
    for (int x = 0; x < xSize; x++)
    {
        xmap[x, y] = lines[y][x];
    }
}

List<Vector2[]> patterns = [
    new [] { new Vector2(0, 0), new Vector2(1, 0), new Vector2(2, 0), new Vector2(3, 0) }, // Horizontal
    new [] { new Vector2(0, 0), new Vector2(0, 1), new Vector2(0, 2), new Vector2(0, 3) }, // Vertical
    new [] { new Vector2(0, 0), new Vector2(1, 1), new Vector2(2, 2), new Vector2(3, 3) }, // Diagonal Right
    new [] { new Vector2(0, 0), new Vector2(-1, 1), new Vector2(-2, 2), new Vector2(-3, 3) }, // Diagonal Left
];

var count = 0;
foreach (var pattern in patterns)
{
    for (var x = 0; x < xSize; x++)
    {
        for (var y = 0; y < ySize; y++)
        {
            var check = GetCheckString(x, y, pattern);
            if (check == "XMAS" || check == "SAMX")
                count++;
        }
    }
}
Console.WriteLine($"Result 1: {count}");

// Part 2
var xPattern = new[] { new Vector2(0, 0), new Vector2(2, 0), new Vector2(1, 1), new Vector2(0, 2), new Vector2(2, 2) }; // X-Pattern..
var wordList = new[] { "MSAMS", "SSAMM", "MMASS", "SMASM" };
var count2 = 0;
for (var x = 0; x < xSize; x++)
{
    for (var y = 0; y < ySize; y++)
    {
        var check = GetCheckString(x, y, xPattern);
        if (wordList.Contains(check))
            count2++;
    }
}
Console.WriteLine($"Result 2: {count2}");

string GetCheckString(int x, int y, Vector2[] pattern)
{
    var check = "";
    for (var i = 0; i < pattern.Length; i++)
    {
        var xPos = x + Convert.ToInt32(pattern[i].X);
        var yPos = y + Convert.ToInt32(pattern[i].Y);
        if (xPos < 0 || xPos >= xSize || yPos < 0 || yPos >= ySize)
            return "";
        check += xmap[xPos, yPos];
    }
    return check;
}
